import React from 'react';
import { Form } from 'react-bootstrap';

const QuestionComponent = () => {
  return (
    <div style={{ border: '1px solid #E5E5E5', padding: '90px' }}>
      <Form.Group>
        <Form.Label className="d-flex justify-content-center">Question:</Form.Label>
        <div className="d-flex justify-content-between align-items-center">
          <span style={{ color: 'red', marginRight: '10px' }}>Disagree</span>
          <div className="d-flex flex-grow-1 justify-content-between">
            {[1, 2, 3, 4, 5, 6, 7].map((degree) => (
              <Form.Check
                key={degree}
                type="radio"
                name="questionOption"
                id={`option${degree}`}
                label={degree.toString()}
                className="ml-2"
              />
            ))}
          </div>
          <span style={{ color: 'green', marginLeft: '10px' }}>Agree</span>
        </div>
      </Form.Group>
    </div>
  );
};

export default QuestionComponent;
