import './styles.scss';
import React, { useContext } from 'react';
import { QuestionProvider } from '../context/QuestionContext';
import QuestionList from '../components/QuestionList';
import { QuestionContext } from '../context/QuestionContext';

export default function Home() {
  const { state, dispatch } = useContext(QuestionContext);


  function handleEmailChange(event) {
    const newEmail = event.target.value;
    dispatch({ type: 'SET_EMAIL', payload: newEmail });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    //console.log(formData); // Replace with your submission logic
  };

  return (
    <QuestionProvider>
      <div className="home-page">
        <h1>Discover Your Perspective</h1>
        <h2>Complete the 7 min test and get a detailed report of your lenses on the world.</h2>
        <form onSubmit={handleSubmit}>
          <div className="content">
            <QuestionList />
            <div className="form-group email-container">
              <label htmlFor="email">Your Email</label>
              <input type="email" id="email" name="email" value={email} onChange={handleEmailChange} required placeholder='you@example.com' />
            </div>
          </div>
          <div className="text-center">
            <button type="submit" className="btn btn-primary">Submit</button>
          </div>
        </form>
      </div>
    </QuestionProvider>
  );
}
